parameters.functionCode:
// 1) Parsear stdout del extractor
const out = JSON.parse($json.stdout);

// 2) Expandir a 1 item por chunk para poder llamar embeddings
return out.chunks.map(c => ({
  json: {
    // datos para embeddings + insert
    source_id: out.source_id,
    chunk_index: c.chunk_index,
    page_number: c.page_number ?? null,
    content: c.content,
    status: c.status || 'ok',

    // conservar paths para mover el archivo si hace falta
    file_path: out.file_path,
    dest_path: out.dest_path,

    // mantener longitud total por si la query la usa
    chunk_text_length: out.text_length || 0
  }
}));

parameters.query:
INSERT INTO kb_sources (source_name, source_path)
VALUES (
  '{{ String($json.source_name || $json.file_name || "documento").replace(/'/g, "''") }}',
  '{{ String($json.source_path || $json.dest_path || $json.file_path || '').replace(/'/g, "''") }}'
)
RETURNING source_id;

parameters.jsCode:
const p = $json.path || '';
const name = p.split('/').pop();
const ext = name.includes('.') ? name.slice(name.lastIndexOf('.') + 1) : '';
return [{
  json: {
    fileName: name,
    fileExtension: ext,
    filePath: p
  }
}];

const ctx = $node["Prepare File Context"].json;

return [
  {
    json: {
      source_id: $json.source_id,   // viene de Insert KB Sources; sin fallback,
      source_name: ctx.fileName || "unknown",
      file_name: ctx.fileName || "unknown",
      file_path: ctx.filePath || "",
      dest_path: ctx.filePath ? `/data/kb/processed/${ctx.fileName}` : "",
      extension: (ctx.fileExtension || "").toLowerCase(),
      source_path: ctx.filePath || ""
    }
  }
];

parameters.functionCode:
// Toma los datos *originales* (antes del HTTP) del nodo "Prepare Chunk Payload"
const src = $item(0).$node["Prepare Chunk Payload"].json;

// De cada item (respuesta del HTTP) extrae el embedding
const chunks = items.map((it) => {
  // El contenido y metadatos vienen del payload original
  const chunk_index = it.json.chunk_index ?? $json.chunk_index;
  const page_number = it.json.page_number ?? $json.page_number;
  const content = it.json.content ?? $json.content;
  const status  = it.json.status  ?? $json.status ?? 'ok';

  // Intentar leer el embedding de la respuesta HTTP
  const data = it.json.data || (it.json.body && it.json.body.data);
  const embedding = (Array.isArray(data) && data[0] && Array.isArray(data[0].embedding))
    ? data[0].embedding
    : it.json.embedding; // por si ya viene directo

  return {
    chunk_index,
    page_number,
    content,
    status,
    embedding, // array 1536
  };
});

return [{
  json: {
    // ¡AQUÍ fijamos el source_id desde el nodo anterior, ya no “undefined”!
    source_id: src.source_id,

    // JSON listo para Postgres
    chunks_json: chunks,

    // opcionales útiles
    chunk_text_length: src.chunk_text_length || 0,
    file_path: src.file_path,
    dest_path: src.dest_path,
  }
}];
