parameters.command:
={{ (() => {
  const src = String($json.src_path || $json.path || $json.filePath || $json.file || '').trim();
  const dest = String($json.clean_dest_path || $json.dest_path || '').trim();
  if (!src || !dest) {
    throw new Error('Missing source/dest path for move');
  }
  const parts = dest.split('/');
  if (parts.length > 1) {
    parts.pop();
  }
  const destDir = parts.join('/') || '/data/kb/processed';
  return `mkdir -p "${destDir}" && mv "${src}" "${dest}"`;
})() }}

