parameters.query:
UPDATE kb_sources
   SET source_path = {{ (() => {
  const value = String($json.clean_dest_path ?? '').trim();
  if (!value) {
    return 'NULL';
  }
  return "'" + value.replace(/'/g, "''") + "'";
})() }}
 WHERE source_id = {{ (() => {
  const numeric = Number($json.clean_source_id);
  return Number.isFinite(numeric) && numeric > 0 ? numeric : -1;
})() }};

assignments.value:
={{ (() => {
  const candidates = [$json.source_id, $json.rows?.[0]?.source_id];
  for (const raw of candidates) {
    const numeric = Number(String(raw ?? '').replace(/\r?\n/g, '').trim());
    if (Number.isFinite(numeric) && numeric > 0) {
      return numeric;
    }
  }
  return -1;
})() }}

={{ (() => {
  const sanitizePath = (value) => String(value ?? '').replace(/\r?\n/g, '').trim();
  const rawDest = sanitizePath($json.dest_path);
  if (rawDest) {
    return rawDest;
  }
  const baseName = sanitizePath($json.dest_name);
  if (baseName) {
    return '/data/kb/processed/' + baseName;
  }
  const primaryName = sanitizePath($json.filename) || sanitizePath($json.fileName) || 'file';
  const fallback = primaryName
    .normalize('NFD').replace(/[\u0300-\u036f]/g, '')
    .replace(/\.pdf$/i, '')
    .replace(/\s+/g, '_')
    .replace(/[^A-Za-z0-9_.-]/g, '');
  const safeName = (fallback || 'file') + '_' + $now.toFormat('yyyyLLdd_HHmmss') + '.pdf';
  return '/data/kb/processed/' + safeName;
})() }}

parameters.command:
=python3 - <<'PY'
import json
import re
import subprocess
import sys

pdf_path = {{ JSON.stringify((String($json.clean_dest_path || $json.dest_path || '').trim())) }}
if not pdf_path:
    print(json.dumps({'error': 'missing_pdf_path', 'chunks': []}))
    sys.exit(0)
cmd = ['pdftotext', '-layout', '-enc', 'UTF-8', pdf_path, '-']
try:
    proc = subprocess.run(cmd, capture_output=True, text=True, check=True)
except FileNotFoundError:
    print(json.dumps({'error': 'pdftotext_not_found', 'chunks': []}))
    sys.exit(0)
except subprocess.CalledProcessError as exc:
    print(json.dumps({
        'error': 'pdftotext_failed',
        'stderr': (exc.stderr or '').strip(),
        'returncode': exc.returncode,
        'chunks': []
    }))
    sys.exit(0)
text = proc.stdout or ''
text_stripped = text.strip()
pages_raw = [page for page in text.split('\f') if page.strip()]
chunks = []
chunk_index = 0
max_chunk = 1200
min_chunk = 180
for page_number, raw_page in enumerate(pages_raw, start=1):
    page_text = raw_page.strip()
    if not page_text:
        continue
    paragraphs = [p.strip() for p in re.split(r'\n\s*\n+', page_text) if p.strip()]
    buffer = ''
    for para in paragraphs:
        segments = [para[i:i + max_chunk] for i in range(0, len(para), max_chunk)] or ['']
        for segment in segments:
            piece = segment.strip()
            if not piece:
                continue
            candidate = f"{buffer}\n\n{piece}".strip() if buffer else piece
            if len(candidate) <= max_chunk:
                buffer = candidate
            else:
                if buffer:
                    chunk_index += 1
                    chunks.append({'chunk_index': chunk_index, 'page_number': page_number, 'content': buffer})
                buffer = piece
    if buffer:
        if len(buffer) < min_chunk and chunks:
            chunks[-1]['content'] = f"{chunks[-1]['content']}\n\n{buffer}".strip()
        else:
            chunk_index += 1
            chunks.append({'chunk_index': chunk_index, 'page_number': page_number, 'content': buffer})
result = {
    'chunks': chunks,
    'page_count': len(pages_raw),
    'text_length': len(text_stripped),
    'ocr_suspect': len(text_stripped) < 80
}
print(json.dumps(result, ensure_ascii=False))
PY

