const item = items[0] || { json: {} };
const raw = item.json.stdout || '';
let parsed = {};
if (raw) {
  try {
    parsed = JSON.parse(raw);
  } catch (error) {
    throw new Error('Chunk command did not return JSON: ' + raw.slice(0, 400));
  }
}
const chunks = Array.isArray(parsed.chunks) ? parsed.chunks : [];
item.json.chunkData = parsed;
item.json.chunkCount = chunks.length;
item.json.chunkError = parsed.error || null;
item.json.needsOcr = Boolean(parsed.ocr_suspect) || chunks.length === 0 || Boolean(item.json.chunkError);
item.json.hasChunks = !item.json.needsOcr && chunks.length > 0;
item.json.chunkStdErr = parsed.stderr || null;
item.json.chunkTextLength = parsed.text_length ?? null;
return items;
