// Fan-Out for Embeddings — 1 item = 1 chunk (liviano, sin duplicar listas grandes)
const itemsIn = $input.all().map(it => it.json?.data ?? it.json);
if (!itemsIn.length) return [];

let meta = {};
let chunks = [];

// Caso: un solo item trae .chunks[]
if (Array.isArray(itemsIn[0]?.chunks) && itemsIn[0].chunks.length) {
  const f = itemsIn[0];
  meta = {
    source_id:   f.source_id   ?? f.sourceId   ?? null,
    source_name: f.source_name ?? f.sourceName ?? null,
    file_name:   f.file_name   ?? f.fileName   ?? null,
    file_path:   f.file_path   ?? f.filePath   ?? null,
    source_path: f.source_path ?? f.sourcePath ?? (f.file_path ?? null),
    dest_path:   f.dest_path   ?? f.destPath   ?? null,
    page_count:  f.page_count  ?? f.pageCount  ?? null,
  };
  chunks = f.chunks.map((c, i) => ({
    chunk_index: c.chunk_index ?? c.chunkIndex ?? (i + 1),
    page_number: c.page_number ?? c.pageNumber ?? 1,
    content:     c.content ?? '',
  }));
} else {
  // Caso: ya viene 1 item por chunk desde Extract
  const f = itemsIn[0] ?? {};
  meta = {
    source_id:   f.source_id   ?? f.sourceId   ?? null,
    source_name: f.source_name ?? f.sourceName ?? null,
    file_name:   f.file_name   ?? f.fileName   ?? null,
    file_path:   f.file_path   ?? f.filePath   ?? null,
    source_path: f.source_path ?? f.sourcePath ?? (f.file_path ?? null),
    dest_path:   f.dest_path   ?? f.destPath   ?? null,
    page_count:  f.page_count  ?? f.pageCount  ?? null,
  };
  chunks = itemsIn.map((j, i) => ({
    chunk_index: j.chunk_index ?? j.chunkIndex ?? (i + 1),
    page_number: j.page_number ?? j.pageNumber ?? 1,
    content:     j.content ?? '',
  }));
}

return chunks.map(c => ({
  json: { ...meta, ...c, text_length: (c.content?.length ?? 0) }
}));

// Get first item from Fan-Out for Embeddings for paths
const it = $items("Fan-Out for Embeddings", 0, 0)[0];
return [{ json: it.json }];

// Meta del mismo índice desde el nodo de texto
const meta = $items('Clone Meta', 0, $itemIndex)[0].json;
// Embedding del HTTP actual
const emb = $json?.data?.[0]?.embedding ?? null;

return [{
  json: {
    source_id:   meta.source_id,
    chunk_index: meta.chunk_index,
    page_number: meta.page_number ?? 1,
    content:     meta.content ?? '',
    embedding:   emb
  }
}];
