// Repack (per-item) — lee embedding desde donde realmente llega la respuesta del HTTP

const meta = $json || {};
const content = typeof meta.content === "string" ? meta.content : "";

// Detectar el embedding en cualquiera de estos sitios
let emb = null;
if (Array.isArray(meta?.data) && Array.isArray(meta.data[0]?.embedding)) {
  emb = meta.data[0].embedding;                 // HTTP → Response Format = JSON (en raíz)
} else if (meta?.body && Array.isArray(meta.body?.data) && Array.isArray(meta.body.data[0]?.embedding)) {
  emb = meta.body.data[0].embedding;            // algunas configs lo ponen en body
} else if (meta?.openai_response && Array.isArray(meta.openai_response?.data)
           && Array.isArray(meta.openai_response.data[0]?.embedding)) {
  emb = meta.openai_response.data[0].embedding; // por si usas un wrapper
} else if (Array.isArray(meta?.embedding)) {
  emb = meta.embedding;                          // proxies
}

return [{
  json: {
    source_id:   meta.source_id   ?? null,
    source_name: meta.source_name ?? null,
    file_name:   meta.file_name   ?? null,
    file_path:   meta.file_path   ?? null,
    source_path: meta.source_path ?? null,
    dest_path:   meta.dest_path   ?? null,
    page_count:  meta.page_count  ?? null,
    text_length: typeof content === "string" ? content.length : (meta.text_length ?? 0),

    chunks_json: [{
      chunk_index: meta.chunk_index ?? meta.chunkIndex ?? 1,
      page_number: meta.page_number ?? meta.pageNumber ?? 1,
      content,
      status: Array.isArray(emb) ? "ok" : "no_embedding",
      embedding: Array.isArray(emb) ? emb : null,
    }],
  }
}];

