// Fan-Out for Embeddings — 1 item = 1 chunk (liviano, sin duplicar listas grandes)
const itemsIn = $input.all().map(it => it.json?.data ?? it.json);
if (!itemsIn.length) return [];

let meta = {};
let chunks = [];

// Caso: un solo item trae .chunks[]
if (Array.isArray(itemsIn[0]?.chunks) && itemsIn[0].chunks.length) {
  const f = itemsIn[0];
  meta = {
    source_id:   f.source_id   ?? f.sourceId   ?? null,
    source_name: f.source_name ?? f.sourceName ?? null,
    file_name:   f.file_name   ?? f.fileName   ?? null,
    file_path:   f.file_path   ?? f.filePath   ?? null,
    source_path: f.source_path ?? f.sourcePath ?? (f.file_path ?? null),
    dest_path:   f.dest_path   ?? f.destPath   ?? null,
    page_count:  f.page_count  ?? f.pageCount  ?? null,
  };
  chunks = f.chunks.map((c, i) => ({
    chunk_index: c.chunk_index ?? c.chunkIndex ?? (i + 1),
    page_number: c.page_number ?? c.pageNumber ?? 1,
    content:     c.content ?? '',
  }));
} else {
  // Caso: ya viene 1 item por chunk desde Extract
  const f = itemsIn[0] ?? {};
  meta = {
    source_id:   f.source_id   ?? f.sourceId   ?? null,
    source_name: f.source_name ?? f.sourceName ?? null,
    file_name:   f.file_name   ?? f.fileName   ?? null,
    file_path:   f.file_path   ?? f.filePath   ?? null,
    source_path: f.source_path ?? f.sourcePath ?? (f.file_path ?? null),
    dest_path:   f.dest_path   ?? f.destPath   ?? null,
    page_count:  f.page_count  ?? f.pageCount  ?? null,
  };
  chunks = itemsIn.map((j, i) => ({
    chunk_index: j.chunk_index ?? j.chunkIndex ?? (i + 1),
    page_number: j.page_number ?? j.pageNumber ?? 1,
    content:     j.content ?? '',
  }));
}

return chunks.map(c => ({
  json: { ...meta, ...c, text_length: (c.content?.length ?? 0) }
}));

// Get first item from Fan-Out for Embeddings for paths
const it = $items("Fan-Out for Embeddings", 0, 0)[0];
return [{ json: it.json }];

const meta = $json ? JSON.parse(JSON.stringify($json)) : {}
return [{
  json: Object.assign({}, meta, { meta }),
  pairedItem: { item: 0 }
}]

const merged = $json || {}
const meta = merged.meta ? { ...merged.meta } : {}
const httpEmbedding = Array.isArray(merged.embedding) ? merged.embedding : null
const fallbackEmbedding = Array.isArray(meta.embedding) ? meta.embedding : null
const embedding = httpEmbedding || fallbackEmbedding || null
const status = merged.status || (httpEmbedding ? 'ok' : (fallbackEmbedding ? 'fallback' : 'no_embedding'))
const chunkIndex = merged.chunk_index != null ? merged.chunk_index : (meta.chunk_index != null ? meta.chunk_index : null)
const pageNumber = merged.page_number != null ? merged.page_number : (meta.page_number != null ? meta.page_number : 1)
const content = merged.content || merged.chunk_text || meta.content || meta.chunk_text || ''
return [{
  json: {
    ...meta,
    ...merged,
    meta,
    chunk_index: chunkIndex,
    page_number: pageNumber,
    content,
    embedding,
    status,
  }
}]

