const src = $json || {}
let embedding = null
const data = Array.isArray(src.data) ? src.data : (src.body && Array.isArray(src.body.data) ? src.body.data : [])
if (Array.isArray(src.embedding)) {
  embedding = src.embedding
} else if (Array.isArray(data) && Array.isArray(data[0] && data[0].embedding)) {
  embedding = data[0].embedding
}
const status = embedding ? 'ok' : (src.status || 'failed')
return [{
  json: embedding ? { embedding } : { embedding: null, status },
  pairedItem: { item: 0 }
}]

if ($json.chunk_index == null || $json.chunk_index <= 0) {
  throw new Error('chunk_index inválido antes del INSERT')
}
return $json

const sourceId = $json.source_id || $json.sourceId
if (sourceId == null) {
  throw new Error('Test Runner: source_id no disponible')
}
const baseChunk = 10
const embeddings = [0.11, 0.22, 0.33, 0.44]
const items = []
for (let i = 0; i < 3; i++) {
  const chunkIndex = baseChunk + i
  const pageNumber = i + 1
  const content = 'Test chunk ' + chunkIndex
  items.push({
    json: {
      meta: {
        source_id: sourceId,
        chunk_index: chunkIndex,
        page_number: pageNumber,
        content,
        chunk_text: content,
        embedding: embeddings
      },
      source_id: sourceId,
      chunk_index: chunkIndex,
      page_number: pageNumber,
      content,
      chunk_text: content,
      test_run: true,
      embedding: embeddings
    }
  })
}
return items
